INSERT INTO `messagemovie` (`movieId`, `mname`, `mgrade`, `mdirector`, `mprotagonist`, `muptime`, `msite`, `mtype`, `mtext`, `mimg`) VALUES
(1, '《007：大破天幕杀机》', '6.9', '萨姆•门德斯', '丹尼尔•克雷格、哈维尔•巴登、拉尔夫•费因斯、娜奥米•哈里斯、贝纳尼丝•玛尔洛、阿尔伯特•芬尼、朱迪•丹奇', '2012/11/9', '美国', '剧情', '影片讲述詹姆斯•邦德执行任务失败生死不明,记载潜伏于恐怖组织内部特工信息的硬盘丢失。行动主要负责人M夫人因此被高层解雇。幸存下来的邦德执行M夫人的命令,在Q博士的协助下,赶赴上海追查真凶,神秘人物席尔瓦引起他的关注', 'http://127.0.0.1:3000/movie/img/1.jpg'),
(2, '《X战警》', '5.8', '布莱恩•辛格', '帕特里克•斯图尔特、休•杰克曼、伊恩•麦克莱恩、哈莉•贝瑞、法米克•詹森、詹姆斯•麦斯登', '2000/7/14', '美国', '动作', '电影讲述一群身怀特异功能的队伍,为了保护人类与变种人的友善关系,极力与邪恶力量作斗争。为了保护自己和人类,X战警只有一条路,战斗到底', 'http://127.0.0.1:3000/movie/img/2.jpg'),
(3, '《阿凡达》', '8.7', '詹姆斯•卡梅隆', '萨姆•沃辛顿、佐伊•索尔达娜和西格妮•韦弗', '2009/12/16', '美国', '科幻', '该片主要讲述人类穿上阿凡达的躯壳,飞到遥远的星球潘多拉开采资源。受伤后以轮椅代步的前海军杰克,自愿接受实验并以他的阿凡达来到潘多拉。在结识了当地纳美族人公主涅提妮之后,杰克在一场人类与潘多拉军民的战争中陷入两难', 'http://127.0.0.1:3000/movie/img/3.jpg'),
(4, '《阿甘正传》', '9.5', '罗伯特•泽米吉斯', '汤姆•汉克斯、罗宾•怀特', '1994/7/6', '美国', '剧情', '电影改编自美国作家温斯顿•格卢姆于1986年出版的同名小说,描绘了先天智障的小镇男孩福瑞斯特•甘自强不息,最终“傻人有傻福”地得到上天眷顾,在多个领域创造奇迹的励志故事', 'http://127.0.0.1:3000/movie/img/4.jpg'),
(5, '《爱丽丝梦游仙境》', '7.0', '蒂姆•波顿', '米娅•华希科沃斯卡、约翰尼•德普、海伦娜•邦汉•卡特和安妮•海瑟薇', '2010/3/5', '美国', '奇幻', '影片讲述了发生在爱丽丝上次梦游仙境的十三年后的故事,已经19岁的爱丽丝去一个庄园参加一个聚会,爱丽丝选择了逃跑,她跟着一只白兔钻进了一个洞,再次来到仙境', 'http://127.0.0.1:3000/movie/img/5.jpg'),
(6, '《傲慢与偏见》', '8.2', '乔•怀特', '凯拉•奈特利,马修•麦克费登,吉娜•马隆', '2005/9/16', '英国', '爱情', '该片讲述了19世纪初期英国的乡绅之女伊丽莎白•班内特五姐妹的爱情与择偶的故事', 'http://127.0.0.1:3000/movie/img/.6jpg'),
(7, '《霸王别姬》', '9.6', '陈凯歌', '张国荣,巩俐,张丰毅,葛优,英达,雷汉', '1993/1/1', '中国', '剧情', '影片围绕两位京剧伶人半个世纪的悲欢离合,展现了对传统文化、人的生存状态及人性的思考与领悟', 'http://127.0.0.1:3000/movie/img/7.jpg'),
(8, '《悲惨世界》', '8.6', '汤姆•霍伯', '休•杰克曼,安妮•海瑟薇,罗素•克劳,阿曼达•塞弗里德,埃迪•雷德梅尼,萨莎•拜伦•科恩', '2013/2/28', '英国', '剧情', '讲述了19世纪的法国,穷苦农民冉阿让因偷面包而入狱多年,出狱后被收留他的主教感化,冉阿让决心洗心革面,开始新生活,却一直受到铁面警官沙威锲而不舍地追捕', 'http://127.0.0.1:3000/movie/img/8.jpg'),
(9, '《变形金刚3：月黑之时》', '7.1', '迈克尔•贝', '希亚•拉博夫、乔什•杜哈明、约翰•特托罗、泰瑞斯•吉布森、罗茜•汉丁顿-惠特莉、帕特里克•德姆西、约翰•马尔科维奇、弗朗西斯•麦克杜蒙德', '2011/6/29', '美国', '科幻', '影片主要讲述了阿波罗11号40年前登陆月球时原来早就发现了变形金刚的残骸,当年博派首领御天敌带着拯救塞伯坦球的秘密武器“能量柱”逃走,不幸坠落在月球。多年后,狂派威震天设下陷阱,让擎天柱带回御天敌和能量柱,掀起两派在地球的战事', 'http://127.0.0.1:3000/movie/img/9.jpg'),
(10, '《变形金刚4：绝迹重生》', '6.6', '迈克尔•贝', '马克•沃尔伯格、斯坦利•图齐、凯尔希•格兰莫、妮可拉•佩尔茨、杰克•莱诺、索非亚•迈尔斯、李冰冰', '2014/6/27', '美国', '科幻', '该片讲述了一群富商巨贾和科学精英试图利用变形金刚的技术来发展人类科技,但他们所取得的成就足以蚕食和毁灭整个世界,与此同时,一个古老而又强大的变形金刚力量正把目标瞄向了地球', 'http://127.0.0.1:3000/movie/img/10.jpg'),
(11, '《辩护人》', '9.2', '杨宇锡', '宋康昊,金英爱,吴达秀,郭道元', '2013/12/18', '韩国', '剧情', '影片以20世纪八十年代的釜山为背景,以已故前总统卢武铉曾在担任律师时,负责釜林事件辩护的故事为原型,讲述了没有钱、没有学历、没有背景的税务律师宋佑硕经历的改变其人生的五次公审', 'http://127.0.0.1:3000/movie/img/11.jpg'),
(12, '《冰雪奇缘》', '7.3', '克里斯•巴克、珍妮弗•李', '克里斯汀•贝尔、伊迪娜•门泽尔', '2013/11/27', '美国', '动画', '该片改编自安徒生童话《白雪皇后》,讲述小国阿伦黛尔因一个魔咒永远地被冰天雪地覆盖,为了寻回夏天,安娜公主和山民克里斯托夫以及他的驯鹿搭档组队出发,展开一段拯救王国的历险', 'http://127.0.0.1:3000/movie/img/12.jpg'),
(13, '《超脱》', '8.9', '托尼•凯耶', '艾德里安•布洛迪,克里斯蒂娜•亨德里克斯', '2011/4/25', '美国', '剧情', '该片讲述孤独的代课老师亨利•巴赫特来到一所散漫的高中任教,在和学生的相处中看到了世界美丽的生活和人性的故事', 'http://127.0.0.1:3000/movie/img/13.jpg'),
(14, '《沉默的羔羊》', '8.8', '乔纳森•戴米', '朱迪•福斯特、安东尼•霍普金斯', '1991/2/14', '美国', '惊悚', '该片讲述了实习特工克拉丽斯为了追寻杀人狂野牛比尔的线索,前往一所监狱访问精神病专家汉尼拔博士,汉尼拔给克拉丽斯提供了一些线索,最终克拉丽斯找到了野牛比尔,并将其击毙', 'http://127.0.0.1:3000/movie/img/14.jpg'),
(15, '《楚门的世界》', '9.2', '彼得•威尔', '金•凯瑞、劳拉•琳妮、诺亚•艾默里奇、艾德•哈里斯', '1998/6/1', '美国', '剧情', '影片讲述了楚门是一档热门肥皂剧的主人公,他身边的所有事情都是虚假的,他的亲人和朋友全都是演员,但他本人对此一无所知。最终楚门不惜一切代价走出了这个虚拟的世界', 'http://127.0.0.1:3000/movie/img/15.jpg'),
(16, '《触不可及》', '9.2', '赵宝刚', '桂纶镁,孙红雷,方中信,徐静蕾,蒋勤勤,黄磊', '2014/9/19', '中国', '剧情', '影片故事背景跨越70年之久,讲述了一段战争时期的旷世恋情。《触不可及》将一位地下工作者与一位舞蹈老师错综的恋情诠释得淋漓尽致,烘托出在战乱时期中的一段谍恋故事', 'http://127.0.0.1:3000/movie/img/16.jpg'),
(17, '《穿条纹睡衣的男孩》', '9.1', '马克•赫曼', '阿沙•巴特菲尔德,维拉•法梅加,大卫•休里斯,鲁伯特•弗兰德,杰克•塞隆', '2008/11/7', '美国', '剧情', '影片讲述二战期间,八岁的布鲁诺是集中营德国司令官的儿子,他与集中营围栏的另一边的一个犹太男孩（Jack Scanlon饰）结下了友谊,从而发生了许多令人意料不到的事情', 'http://127.0.0.1:3000/movie/img/17.jpg'),
(18, '《当幸福来敲门》', '9.1', '加布里尔•穆奇诺', '威尔•史密斯、贾登•史密斯、桑迪•牛顿', '2006/12/15', '美国', '剧情', '影片讲述了一位濒临破产、老婆离家的落魄业务员,如何刻苦耐劳的善尽单亲责任,奋发向上成为股市交易员,最后成为知名的金融投资家的励志故事', 'http://127.0.0.1:3000/movie/img/18.jpg'),
(19, '《盗梦空间》', '9.3', '克里斯托弗•诺兰', '莱昂纳多•迪卡普里奥,玛丽昂•歌迪亚', '2010/9/2', '美国', '科幻', '影片讲述由莱昂纳多•迪卡普里奥扮演的造梦师,带领约瑟夫•高登-莱维特、艾伦•佩吉扮演的特工团队,进入他人梦境,从他人的潜意识中盗取机密,并重塑他人梦境的故事', 'http://127.0.0.1:3000/movie/img/19.jpg'),
(20, '《第九区》', '8.2', '尼尔•布洛姆坎普', '沙尔托•科普雷,詹森•库伯,威廉•艾伦•扬', '2009/8/14', '美国', '科幻', '电影讲述了维库斯感染了某种神秘病毒,正逐渐变成一个外星人,为了不沦为研究的对象遭受非人的实验,他只有逃往唯一安全之地——第九区', 'http://127.0.0.1:3000/movie/img/20.jpg'),
(21, '《谍影重重3》', '8.8', '保罗•格林格拉斯', '马特•达蒙,朱丽娅•斯蒂尔斯', '2007/8/3', '美国', '悬疑', '影片剧情承接《谍影重重2》中莫斯科的激烈车战,随着伯恩来到巴黎、伦敦、马德里、丹吉尔到最后的纽约市,而CIA则会派出更强、更冷血的杀手追杀伯恩', 'http://127.0.0.1:3000/movie/img/21.jpg'),
(22, '《碟中谍》', '8.1', '布莱恩•德•帕尔玛', '汤姆•克鲁斯,强•沃特,亨利•科泽尼,艾曼纽•贝阿,让•雷诺,文•瑞姆斯,克里斯汀•斯科特•托马斯,瓦妮莎•雷德格瑞夫,马塞尔•尤勒斯,扬•卡拉米特鲁', '1996/5/22', '美国', '冒险', '故事围绕美国“不可能的任务情报署（IMF）”展开。该情报署的特工都身手不凡,而且他们也擅长使用易容术,让目标在不知情的情况下供出情报', 'http://127.0.0.1:3000/movie/img/22.jpg'),
(23, '《独立日》', '8', '罗兰•艾默里奇', '威尔•史密斯、比尔•普尔曼和杰夫•高布伦', '1996/7/3', '美国', '动作', '影片主要讲述一艘巨型的外星人母船进入地球轨道,并释放了三十多个小型飞船进入地球大气层,停留在世界几大城市上空,造成人们的恐慌。美国总统（比尔•普尔曼饰）联合各国领袖共商解决之道,科学家（杰夫•高布伦饰）和陆战队航空兵上尉史蒂芬希尔（威尔•史密斯饰）合作为人类的命运奋斗,从而阻止外星人入侵,捍卫地球的故事', 'http://127.0.0.1:3000/movie/img/23.jpg'),
(24, '《放牛班的春天》', '9.3', '克里斯托夫•巴拉蒂', '杰拉尔•朱诺,狄迪尔•弗拉蒙,雅克•贝汉,玛丽•布奈尔', '2004/3/17', '法国', '音乐', '讲述的是一位怀才不遇的音乐老师马修来到辅育院,面对的不是普通学生,而是一群被大人放弃的野男孩,马修改变了孩子以及他自己的命运的故事', 'http://127.0.0.1:3000/movie/img/24.jpg'),
(25, '《风云人物》', '9.2', '弗兰克•卡普拉', '詹姆斯•史都华、唐娜•里德、莱昂纳尔•巴里摩尔', '1946/12/20', '美国', '剧情', '影片讲述了主人公乔治在圣诞夜丧失了对生活的信心,准备自杀。于是,上帝派了一个天使,来帮他渡过这个危机。在天使的指引下,乔治看到了如果自己没有来到这个世界,很多人的人生会变得不幸和痛苦。他由此明白了自己生命的价值何在,重新鼓起了生活的勇气的故事', 'http://127.0.0.1:3000/movie/img/25.jpg'),
(26, '《风之谷》', '8.8', '宫崎骏', '岛本须美,纳谷悟朗,松田洋治,辻村真人', '1984/3/11', '日本', '动画', '影片讲述千年前世界的产业文明达致巅峰后,经历一场称为“火之七日”的战争而毁于一旦。世界被一种由菌类构成,名叫腐海的新生态体系所掩盖,只有巨型昆虫能够适应其中,而人类们生活在仅存的小面积土地上,在面对巨型昆虫和会释放瘴气的腐海森林包围威胁下积极求存的故事', 'http://127.0.0.1:3000/movie/img/26.jpg'),
(27, '《疯狂动物城》', '9.2', '里奇•摩尔、拜恩•霍华德及杰拉德•布什', '金妮弗•古德温、杰森•贝特曼、夏奇拉、艾伦•图代克、伊德瑞斯•艾尔巴、J•K•西蒙斯', '2016/3/4', '美国', '动画', '该片讲述了在一个所有动物和平共处的动物城市,兔子朱迪通过自己努力奋斗完成自己儿时的梦想,成为动物警察的故事', 'http://127.0.0.1:3000/movie/img/27.jpg'),
(28, '《复仇者联盟》', '8.2', '乔斯•韦登', '小罗伯特•唐尼、克里斯•埃文斯、克里斯•海姆斯沃斯', '2012/5/1', '美国', '科幻', '一股突如其来的强大邪恶势力对地球造成致命威胁,没有任何一个超级英雄能够单独抵挡。长期致力于保护全球安危的神盾局感到措手不及,其指挥官“独眼侠”尼克•弗瑞（塞缪尔•杰克逊 饰）意识到他必须创建一个“史上最强”的联盟组织,才能拯救世界于水深火热,抵御黑暗势力的侵袭', 'http://127.0.0.1:3000/movie/img/28.jpg'),
(29, '《复仇者联盟2奥创纪元》', '7.4', '乔斯•韦登', '小罗伯特•唐尼、克里斯•海姆斯沃斯、马克•鲁法洛、克里斯•埃文斯、斯嘉丽•约翰逊和杰瑞米•雷纳', '2015/5/1', '美国', '科幻', '超级英雄们逐渐产生了职业倦怠,已经卸下钢铁战甲的托尼•斯塔克因此发明了“奥创”——有自我意识、有学习能力的人工智能机器人,并将指挥机器人军团的重任交给奥创。令超级英雄们始料不及的是,不断进化的奥创得出了“人类是地球上最大的威胁”这一结论,进而开始实施清洗人类的毁灭计划。有强大能力的改造者马克西莫夫兄妹快银皮特罗和猩红女巫旺达也成为奥创的帮手,四处制造麻烦。复仇者联盟必须再度集合,解决这个由他们亲手制造的危机', 'http://127.0.0.1:3000/movie/img/29.jpg'),
(30, '《复仇者联盟3无限战争》', '8.1', '罗素兄弟', '小罗伯特•唐尼、乔什•布洛林、克里斯•埃文斯、克里斯•海姆斯沃斯、斯嘉丽•约翰逊、马克•鲁法洛', '2018/4/1', '美国', '科幻', '复仇者联盟及其盟友们牺牲一切,尽全力阻止灭霸收集六颗无限宝石而毁灭一半宇宙的故事', 'http://127.0.0.1:3000/movie/img/30.jpg'),
(31, '《复仇者联盟4终局之战》', '8.5', '罗素兄弟', '小罗伯特•唐尼、克里斯•埃文斯、克里斯•海姆斯沃斯、马克•鲁法洛、斯嘉丽•约翰逊、杰瑞米•雷纳、保罗•路德、布丽•拉尔森、唐•钱德尔、凯伦•吉兰、乔什•布洛林', '2019/4/24', '美国', '科幻', '在灭霸消灭宇宙一半的生灵并重创复仇者联盟之后,剩余的英雄被迫背水一战,为22部漫威电影写下传奇终章', 'http://127.0.0.1:3000/movie/img/31.jpg'),
